import React, { useRef } from 'react';
import { useDispatch } from "react-redux";
import { AddTodo } from "../../redux/state/todo/todoSlice";

const CreateTodo = () => {
    const dispatch = useDispatch();
    const taskInput = useRef();

    return (
        <div className="container mx-auto">
            <div className="flex flex-col md:flex-row items-center justify-between">
                <div className="w-full md:w-3/4 md:mr-4 mb-4 md:mb-0">
                    <input ref={taskInput} type="text" placeholder="Task Name" className="w-full py-2 px-4 border border-gray-300 rounded-md focus:outline-none focus:border-blue-500"/>
                </div>
                <div className="w-full md:w-auto">
                    <button onClick={() => dispatch(AddTodo(taskInput.current.value))} className="w-full md:w-auto py-2 px-4 bg-blue-500 text-white rounded-md transition duration-300 hover:bg-blue-600 focus:outline-none focus:bg-blue-600">
                        Add To Do
                    </button>
                </div>
            </div>
        </div>
    );
};

export default CreateTodo;
